queries.query:
SELECT ingredientname
FROM ingredients
WHERE ingredientid IN (SELECT unnest(?));

SELECT productname
FROM products
WHERE array_length(ingredientids, 1) > 1;

SELECT productname
FROM products
WHERE array_length(ingredientids, 1) <= 1;

SELECT saleprice
FROM products
WHERE productname = ?;

SELECT ismanager
FROM employees
WHERE employeeid = ?;

SELECT firstname
FROM employees
WHERE employeeid = ?;

SELECT productid
FROM products
WHERE productname = ?;
